package test01;

public class Testjava_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10; // 정수형 변수 a를 선언하고 10을 지정
		double b = 10.5; // 실수형 변수 b를 선언하고 10을 지정
		int c;
		c = 100;
		
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		
		int result = a + c;
		System.out.println(result);
		
		int value;
		value = 10;
		int res = value + 10;
		
		System.out.println(res);  // res의 값은 20 입니다.
		System.out.println("res의 값은" + res + "입니다.");
		
		
	}

}
